<template>
    <div class="wepy_icon" :class="_class" :style="_style"></div>
</template>

<script>
  /**
   * [valid description] check props
   * @type {Object}
   */
  const valid = {
    type (v) {
      return !!['success', 'success_no_circle', 'info', 'warn', 'waiting', 'cancel', 'download', 'search', 'clear'].find(key => key === v)
    }
  }

  export default {
    name: 'icon',

    props: {
      'type': {
        type: String,
        default: '',
        validator (v) {
          return valid.type(v)
        }
      },
      'size': {
        type: [ Number, String ],
        default: 23
      },
      'color': {
        type: String,
        default: ''
      }
    },

    computed: {
      _class () {
        if (valid.type(this.type)) {
          return {
            [`wepy_icon-${this.type}`]: true
          }
        }
      },
      _style () {
        return {
          color: this.color,
          fontSize: `${this.size}px`
        }
      }
    }
  }
</script>

